var getTimestamp = function () {
    var date = new Date();
    date.setHours(date.getHours() + 9);
    var dt = date.getFullYear() + '-';
    dt += ("0"+(date.getMonth()+1)).slice(-2) + '-';
    dt += ("0"+date.getDate()).slice(-2) + 'T';
    dt += ("0"+date.getHours()).slice(-2) + ':';
    dt += ("0"+date.getMinutes()).slice(-2) + ':';
    dt += ("0"+date.getSeconds()).slice(-2) + '+09:00';
    return dt;
}

